-----------------Role action mappings to validate create contractor bill for LOA numbers----------------------
insert into eg_action (ID,NAME,URL,QUERYPARAMS,PARENTMODULE,ORDERNUMBER,DISPLAYNAME,ENABLED,CONTEXTROOT,VERSION,CREATEDBY,CREATEDDATE,LASTMODIFIEDBY,LASTMODIFIEDDATE,APPLICATION) values(nextval('SEQ_EG_ACTION'),'AjaxCreateContractorBillValidate','/letterofacceptance/ajaxvalidate-createcontractorbill',null,(select id from eg_module where name='WorksLetterOfAcceptance'),1,'AjaxCreateContractorBillValidate','false','egworks',0,1,now(),1,now(),(select id from eg_module  where name = 'Works Management'));
insert into eg_roleaction values((select id from eg_role where name='Super User'),(select id from eg_action where name='AjaxCreateContractorBillValidate'));
insert into eg_roleaction (roleid, actionid) values ((select id from eg_role where name = 'Works Creator'),(select id from eg_action where name ='AjaxCreateContractorBillValidate' and contextroot = 'egworks'));

